mapper.select:
select p.product_id, p.product_product_upper_category_id, p.product_lower_category_id as p_product_lower_category_id, p.product_name, p.product_code, p.price, p.promotion_price, p.product_register_id, p.create_date, p.description, p.product_img, p.etc, u.user_id, u.name, count(*) over () as totalCount, plc.product_lower_category_id as plc_product_lower_category_id, plc.product_upper_category_id as plc_product_upper_category_id, puc.product_upper_category_id as puc_product_upper_category_id, puc.product_upper_category_name as puc_product_upper_category_name from tb_product p left outer join tb_user u on p.product_register_id = u.user_id left outer join tb_product_lower_category plc on p.product_lower_category_id = plc.product_lower_category_id left outer join tb_product_upper_category puc on plc.product_upper_category_id = puc.product_upper_category_id where 1 = 1
and puc.product_upper_category_id = #{filter}
and (p.product_code like concat('%', trim(ifnull(#{searchValue}, '')), '%') or p.product_name like concat('%', trim(ifnull(#{searchValue}, '')), '%'))
and p.product_code like concat('%', trim(ifnull(#{searchValue}, '')), '%')
and p.product_name like concat('%', trim(ifnull(#{searchValue}, '')), '%')
limit #{startIndex}, #{limit}
